import React, { Component } from 'react';
import Table1 from "./components/tablecomponent";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)
    state = {
      character: [
        {
          id: '1',
          name: 'Charlie',
          job: 'Janitor',
        },
        {
          id: '2',
          name: 'Mac',
          job: 'Bouncer',
        },
        {
          id: '3',
          name: 'Dee',
          job: 'Aspring actress',
        },
        {
          id: '4',
          name: 'Dennis',
          job: 'Bartender',
        },
      ],

    }
    this.removeCharacter = this.removeCharacter.bind(this);

  }

  removeCharacter = (index) => {
    const characters = this.state;
    this.setState({
      characters: characters.filter((i) => {
        return i !== index;
      }),
    })

  }
  render() {
    const characters = this.state
    return (
      <div className="App">
        <Table1 characterData={characters} removeCharacter={this.removeCharacter} />
      </div>

    );
  }
}

export default App;
