import React, { Component } from 'react';
import { ModalHeader, Modal, ModalBody, Button, Form, FormGroup, Input, Label } from 'reactstrap';



class Comment extends Component {
    constructor(props) {
        super(props);

        this.state = {
            FullName: '',
            //isOpen: false,
            isModelOpen: false
        };
        this.commenentModal = this.commenentModal.bind(this);
    }
    commenentModal() {
        this.setState({
            isModelOpen: !this.state.isModelOpen

        });
    }
    render() {

        function RenderComment() {
            return (
                <Modal isOpen={this.state.isModelOpen} toggle={this.commenentModal}>
                    <ModalHeader toggle={this.commenentModal}>Submit Comment</ModalHeader>
                </Modal>

            );
        }

        return (
            <>
                <Button outline onClick={() => this.commenentModal}>
                    <span className="fa fa-pencil fa-lg "></span>   Submit Comment
                </Button>

                <RenderComment />

            </>
        );
    }
}


export default Comment;
