import React, { Component } from 'react';
import { ModalHeader, Modal, ModalBody, Button, Form, FormGroup, Input, Label } from 'reactstrap';


class Comment extends Component {
    constructor(props) {
        super(props);

        this.state = {
            FullName: '',
            isOpen: false,
            isModelOpen: false
        };
        this.commenentModal = this.commenentModal.bind(this);
    }
    commenentModal() {
        this.setState({
            isOpen: !this.state.isOpen

        });
    }
    render() {
        return (
            <>
                <Button outline onClick={() => this.commenentModal}>
                    <span className="fa fa-pencil fa-lg "></span>   Submit Comment
            </Button>

                <Modal isOpen={this.state.isOpen} toggle={this.commenentModal}>
                    <ModalHeader toggle={this.commenentModal}>Comment</ModalHeader>
                </Modal>

            </>
        );
    }
}


export default Comment;
