import React, { Component } from 'react';
import { Modal, ModalHeader, ModalBody, Button, Row, Col, Label } from 'reactstrap';
import { LocalForm, Control } from 'react-redux-form';

const Requird =

    class Comment extends Component {
        constructor(props) {
            super(props);

            this.state = {
                //FullName: '',
                // message: '',
                //isOpen: false,
                open: false
            };
            this.commenentModal = this.commenentModal.bind(this);
        };
        commenentModal() {
            // console.log("hiii");
            //alert("hiii");
            this.setState({
                open: !this.state.open

            });
        }
        commentDisplay = (values) => {
            //console.log('Current State is: ' + JSON.stringify(values));
            alert('Current State is: ' + JSON.stringify(values));

        }
        render() {
            return (
                <div>
                    <Button outline onClick={this.commenentModal}>
                        <span className="fa fa-pencil fa-lg "></span> Submit Comment
                </Button>

                    <Modal isOpen={this.state.open} toggle={this.commenentModal}>
                        <ModalHeader toggle={this.commenentModal}>Submit Comment</ModalHeader>
                        <ModalBody>
                            <LocalForm onSubmit={(values) => this.commentDisplay(values)}>
                                <Label htmlFor="rating">Rating</Label>
                                <Row className="form-group m-1">
                                    <Control.select model=".rating" name="rating"
                                        // value={this.state.contactType}
                                        // onChange={this.handleInputChange}
                                        className="form-control">
                                        <option>1</option>
                                        <option>2</option>
                                        <option>3</option>
                                        <option>4</option>
                                        <option>5</option>
                                    </Control.select >
                                </Row>
                                <Label htmlFor="yourname">Your Name</Label>
                                <Row className="form-group m-1">
                                    <Control.text model=".yourname" id="yourname" name="yourname"
                                        className="form-control"
                                        placeholder="Your Name"
                                    // value={this.state.message}
                                    // onChange={this.handleInputChange}
                                    ></Control.text>

                                </Row>

                                <Label htmlFor="message">Comment</Label>
                                <Row className="form-group m-1">
                                    <Control.textarea model=".message" id="message" name="message"
                                        rows="6"
                                        className="form-control"
                                    // value={this.state.message}
                                    // onChange={this.handleInputChange}
                                    ></Control.textarea>

                                </Row>

                                <Row className="form-group">
                                    <Col md={{ size: 5 }}>
                                        <Button type="submit" color="primary">
                                            Submit
                                    </Button>
                                    </Col>
                                </Row>
                            </LocalForm>

                        </ModalBody>

                    </Modal>

                </div>
            );
        }
    }


export default Comment;
