import React, { Component } from 'react';
import Table1 from "./components/tablecomponent";
import './App.css';

class App extends Component {
  constructor() {
    super()
    this.state =
      [
        {
          id: '1',
          name: 'Charlie',
          job: 'Janitor',
        },
        {
          id: '2',
          name: 'Mac',
          job: 'Bouncer',
        },
        {
          id: '3',
          name: 'Dee',
          job: 'Aspring actress',
        },
        {
          id: '4',
          name: 'Dennis',
          job: 'Bartender',
        },
      ]
  }

  removeCharacter = (index) => {
    const newstate = this.state;
    this.setState({
      newstate: newstate.filter((character, i) => {
        return i !== index
      }),
    });

  }
  render() {
    const names = this.state
    return (
      <div className="App">
        <Table1 characterData={names} removeCharacter={this.removeCharacter(index)} />
      </div>

    );
  }
}

export default App;
