import  React, { Component } from "react";
import {Card, CardBody,CardImg, CardText,CardTitle,CardImgOverlay} from 'reactstrap';
import {DishDetail} from './DishdetailComponent';

class Menu extends Component{
    constructor(props){
        super(props);

        this.state = {
            selectedDish :null
            
        };

    }
    onDishSelect = (dish) => {
        this.setState({
            selectedDish:dish
        });
    }
