import React, { Component } from 'react';
import { Modal, ModalHeader, ModalBody, Button, Row, Label } from 'reactstrap';
import { LocalForm, Control } from 'react-redux-form';



class Comment extends Component {
    constructor(props) {
        super(props);

        this.state = {
            //FullName: '',
            // message: '',
            //isOpen: false,
            open: false
        };
        this.commenentModal = this.commenentModal.bind(this);
    };
    commenentModal() {
        // console.log("hiii");
        //alert("hiii");
        this.setState({
            open: !this.state.open

        });
    }
    render() {
        return (
            <div>
                <Button outline onClick={this.commenentModal}>
                    <span className="fa fa-pencil fa-lg "></span> Submit Comment
                </Button>

                <Modal isOpen={this.state.open} toggle={this.commenentModal}>
                    <ModalHeader toggle={this.commenentModal}>Submit Comment</ModalHeader>
                    <ModalBody>
                        <LocalForm>
                            <Row className="form-group">
                                <Col md={{ size: 10, offset: 2 }}>
                                    <Button type="submit" color="primary">
                                        Send Feedback
                                    </Button>
                                </Col>
                            </Row>
                        </LocalForm>

                    </ModalBody>

                </Modal>

            </div>
        );
    }
}


export default Comment;
